# This class represents a rule within the game\n\nclass Rule:\n    def __init__(self, rule_id, description, is_active=True, **kwargs):\n        self.rule_id = rule_id\n        self.description = description\n        self.is_active = is_active\n        # Additional attributes can be added as needed\n        for key, value in kwargs.items():\n            setattr(self, key, value)\n\n    def activate(self):\n        \"\"\"Activate the rule.\"\"\"\n        self.is_active = True\n\n    def deactivate(self):\n        \"\"\"Deactivate the rule.\"\"\"\n        self.is_active = False\n\n    def __str__(self):\n        return f'Rule({self.rule_id}): {self.description} - {"Active" if self.is_active else "Inactive"}'